// 1. 입력 가져오기
let raw = $input.first().json.output;

// 2. 코드블록( ```json ... ``` ) 제거
if (typeof raw === "string") {
  raw = raw.replace(/```json\s*/g, "").replace(/```/g, "").trim();
}

// 3. 문자열을 JSON으로 파싱
let parsed;
try {
  parsed = JSON.parse(raw);
} catch (e) {
  parsed = { quizes: [] };
}

// 4. 결과 반환 (불필요한 wrapping 없이)
return parsed;
